import numpy as np
import matplotlib.pyplot as plt


def printPlayerDate(color, variable_name, player_name, data, graphColor):
    print("{} {} {}".format(player_name, variable_name, np.mean(data)))
    plt.plot(data, graphColor, label=player_name)
    plt.xlabel('Turnos', fontsize=16)
    plt.ylabel(variable_name, fontsize=16)
    plt.tight_layout()
    plt.legend(loc=1, prop={'size': 15})
    plt.tick_params(axis='both', which='major', labelsize=15)
    plt.tick_params(axis='both', which='minor', labelsize=15)

    return  np.mean(data)


def printInformation(data, variable_name):
    fig1 = plt.figure(figsize=(10, 4), dpi=120)
    data1 = data[0::2]
    data2 = data[1::2]
    avg1 = printPlayerDate("plot", variable_name, "Blancas", data1, 'yo')
    avg2 = printPlayerDate("plot", variable_name, "Negras", data2, 'ko')
    plt.show()
    return avg1, avg2


def analize(current_times, current_ndx_e):
    times_differences = [current_times[i] - current_times[i + 1] for i in range(len(current_times) - 1)]
    current_ndx_e = current_ndx_e[:-1]

    times_differences.reverse()
    current_ndx_e.reverse()

    times_differences = list(map(lambda x: x/1000, times_differences))

    time_avg1, time_avg2 = printInformation(times_differences, 'Tiempo(s)')
    n_avg1, n_avg2 = printInformation(current_ndx_e, 'Boards explored')

    return time_avg1, time_avg2, n_avg1, n_avg2

